  </header>
);

const main = (
  <main>
    <p>This is the main content</p>
  </main>
);

const footer = () => (
  <footer>
    <p>This is the footer</p>
  </footer>
);

const app = (
  {header, main, footer}
)

//render app to the DOM
ReactDOM.render(app, document.getElementById('root'));


};

