import ReactDOM from 'react-dom';
import { useState } from 'react';
import './App.css';

function hex(){
    const [color, setColor] = useState('#000000');

    const getRbg = () => {
        const r = Math.floor(Math.random() * 256);
        const g = Math.floor(Math.random() * 256);
        const b = Math.floor(Math.random() * 256);
        const rgb = `rgb(${r}, ${g}, ${b})`;
        setColor(rgb);
    }
}

export default hex;
