import ReactDOM from 'react-dom';
import  React, {useState}  from 'react';
import './App.css';

function hex(){

const buttonStyles = {
    fontSize: '1.5rem',
    padding: '10px 20px',
    background: 'rgb(0, 255, 0',
    border: 'none',
    borderRadius: 5,
    color: 'white'
}

const Button = () => <button style={buttonStyles}> TESTING </button>



const hexaColor = () => {
    let str = '0123456789abcdef'
    let color = ''

    for(let i = 0; i < 6; i++){
        color += str[Math.floor(Math.random() * str.length)]
    }
    return '#' + color;
}

const hexClr = () => <div>{hexaColor()}</div>

ReactDOM.render(hexClr(), document.getElementById('root'));

}



export default hex;
