import ReactDOM from 'react-dom';
import  useState  from 'react';
import './App.css';

const App = function hex(){
    const [color, setColor] = useState('#000000');

    const getRbg = () => {
        const r = Math.floor(Math.random() * 256);
        const g = Math.floor(Math.random() * 256);
        const b = Math.floor(Math.random() * 256);
        const rgb = `rgb(${r}, ${g}, ${b})`;
        setColor(rgb);
    }

    const rgbToHex = (rgb) => {
        const hex = Number(rgb).toString(16);
        if (hex.length < 2) {
            return `0${hex}`;
        }
        return hex;
    }

    return (
        <div>
            <h1>{color}</h1>
            <button onClick={getRbg}>Generate</button>
            <h2>{rgbToHex(color)}</h2>
        </div>
    )


}

export default hex;
